import React, { useState } from 'react';
import { GrAdd } from 'react-icons/gr';
import './style.css';

function Faq() {
  // Define your FAQ items with unique IDs
  const faqItems = [
    {
      id: 1,
      question: 'What is Afrimart?',
      answer:
        'Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula...',
    },
    {
      id: 2,
      question: 'How does Afrimart work?',
      answer:
        'Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula...',
    },
    // Add more FAQ items here with unique IDs
  ];

  // Initialize all FAQ items to false
  const initialOpenState = {};

  // Set the initial state for openAns to an empty object
  const [openAns, setOpenAns] = useState({});

  const handleOpenAns = (id) => {
    setOpenAns((prevState) => ({
      ...prevState,
      [id]: !prevState[id],
    }));
  };

  return (
    <div className='Faq'>
      <div className="header">
        <h4>FAQ</h4>
      </div>
      <div className="faqBody">
        {faqItems.map((item) => (
          <div className="faqText" key={item.id}>
            <div className="faq_Que">
              <span className="queText">{item.question}</span>
              <GrAdd
                className={`faqIcon ${openAns[item.id] ? 'open' : ''}`}
                onClick={() => handleOpenAns(item.id)}
              />
            </div>
            <div className={`faq_ans ${openAns[item.id] ? 'open' : ''}`}>
              <span className="queAns">{item.answer}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Faq;
